create or replace function countEquivGold (pCharacterId in number) return number is  
vCP number; 
vSP number; 
vGP number; 
vEP number; 
vPP number; 
vEquivGold number(8,2); 
begin 
	select copperPieces, silverPieces, goldPieces, electraPieces, platinumPieces
	into vCP, vSP, vGP, vEP, vPP 
	from Characters
	where CharacterId=pCharacterId; 
	 
	vEquivGold:=vCp/100+vSP/10+vGP+vEP/2+vPP*10; 
	return vEquivGold; 
end countEquivGold;

create or replace function countEquivGoldLoot (pLootId in number) return number is  
vCP number; 
vSP number; 
vGP number; 
vEP number; 
vPP number; 
vEquivGold number(8,2); 
begin 
	select copperPiecesFound, silverPiecesFound, goldPiecesFound, electraPiecesFound, platinumPiecesFound 
	into vCP, vSP, vGP, vEP, vPP 
	from Loot
	where LootId=pLootId; 
	 
	vEquivGold:=vCp/100+vSP/10+vGP+vEP/2+vPP*10; 
	return vEquivGold; 
end countEquivGoldLoot;

