AS 
    v_weapon_count NUMBER; 
BEGIN 
    SELECT COUNT(*) 
    INTO v_weapon_count 
    FROM weapons 
    WHERE CHARACTERWIELDING = p_characterwielding; 
 
    RETURN v_weapon_count; 
END countWeapons;

create or replace FUNCTION GetClassesForSpell(p_spellname VARCHAR2)
RETURN VARCHAR2 IS
    v_class_list VARCHAR2(4000);
BEGIN
    SELECT LISTAGG(classname || ', ') WITHIN GROUP (ORDER BY spell, classname)
    INTO v_class_list
    FROM (select distinct spell, classname from spellsclasses)
    WHERE SPELL = p_spellname;

    IF v_class_list IS NOT NULL AND LENGTH(v_class_list) > 0 THEN
        v_class_list := SUBSTR(v_class_list, 1, LENGTH(v_class_list) - 2);
    END IF;

    RETURN v_class_list;
END;

create or replace FUNCTION split_string(p_list IN VARCHAR2)
   RETURN t_varchar2_tbl PIPELINED
IS
BEGIN
   FOR l_rec IN (
      SELECT TRIM(REGEXP_SUBSTR(p_list, '[^,]+', 1, LEVEL)) AS element
      FROM dual
      CONNECT BY REGEXP_SUBSTR(p_list, '[^,]+', 1, LEVEL) IS NOT NULL
   ) LOOP
      PIPE ROW (l_rec.element);
   END LOOP;
   RETURN;
END split_string;

