create or replace TRIGGER accountLootDelete
FOR DELETE ON loot
COMPOUND TRIGGER

    TYPE T_Record IS RECORD (
        characterId CHARACTERS.CHARACTERID%TYPE,
        copperPieces CHARACTERS.COPPERPIECES%TYPE,
        silverPieces CHARACTERS.SILVERPIECES%TYPE,
        goldPieces CHARACTERS.GOLDPIECES%TYPE,
        electraPieces CHARACTERS.ELECTRAPIECES%TYPE,
        platinumPieces CHARACTERS.PLATINUMPIECES%TYPE
    );
    TYPE T_Table IS TABLE OF T_Record INDEX BY PLS_INTEGER;
    v_records T_Table;

    AFTER EACH ROW IS
    BEGIN
        v_records(v_records.COUNT + 1).characterId := :OLD.characterlooting;
        v_records(v_records.COUNT).copperPieces := :OLD.copperPiecesFound;
        v_records(v_records.COUNT).silverPieces := :OLD.silverPiecesFound;
        v_records(v_records.COUNT).goldPieces := :OLD.goldPiecesFound;
        v_records(v_records.COUNT).electraPieces := :OLD.electraPiecesFound;
        v_records(v_records.COUNT).platinumPieces := :OLD.platinumPiecesFound;
    END AFTER EACH ROW;

    AFTER STATEMENT IS
    BEGIN
        FOR i IN 1 .. v_records.COUNT LOOP
            UPDATE characters
            SET
                copperPieces = copperPieces - v_records(i).copperPieces,
                silverPieces = silverPieces - v_records(i).silverPieces,
                goldPieces = goldPieces - v_records(i).goldPieces,
                electraPieces = electraPieces - v_records(i).electraPieces,
                platinumPieces = platinumPieces - v_records(i).platinumPieces
            WHERE
                characterid = v_records(i).characterId;
        END LOOP;
    END AFTER STATEMENT;

END accountLootDelete;

create or replace trigger accountLootUpdate 
after insert on loot 
for each row 
declare 
cpf number(8) := :NEW.copperPiecesFound; 
spf number(8) := :NEW.silverPiecesFound; 
gpf number(8) := :NEW.goldPiecesFound; 
epf number(8) := :NEW.electraPiecesFound; 
ppf number(8) := :NEW.platinumPiecesFound; 
cl number(6) := :NEW.characterlooting; 
begin 
    update characters 
    set 
        copperPieces = copperPieces + cpf, 
        silverPieces = silverPieces + spf, 
        goldPieces = goldPieces + gpf, 
        electraPieces = electraPieces + epf, 
        platinumPieces = platinumPieces + ppf 
    where 
        characterid=cl; 
end;

create or replace TRIGGER AppendCharacterStatus 
AFTER INSERT ON Events 
FOR EACH ROW 
BEGIN 
    IF :NEW.actionInflicted NOT IN ('Cleared', 'Break free', 'Let go', 'Grappled', 'Attacked', 'Healed') THEN 
        UPDATE Characters 
        SET status = CASE 
                        WHEN status IS NULL THEN :NEW.actionInflicted 
                        WHEN INSTR(status, :NEW.actionInflicted) = 0 THEN status || ', ' || :NEW.actionInflicted 
                        ELSE status 
                     END 
        WHERE characterId = :NEW.character2; 
    END IF; 
END;

