create or replace TRIGGER UpdateCharacterStatusGrappleCase 
AFTER INSERT ON Events 
FOR EACH ROW 
DECLARE 
    character1Exists NUMBER;
    character1Name VARCHAR2(100); 
    character2Name VARCHAR2(100); 
BEGIN 
    select count(*) into character1Exists from characters where characterid = :NEW.character1;
    IF character1Exists != 0 THEN
        SELECT charactername INTO character1Name FROM Characters WHERE characterId = :NEW.character1; 
        SELECT charactername INTO character2Name FROM Characters WHERE characterId = :NEW.character2; 

        IF :NEW.actionInflicted = 'Grappled' AND :NEW.character1 IS NOT NULL THEN 
            UPDATE Characters 
            SET status = CASE  
                            WHEN status IS NULL THEN 'Grappling ' || character2Name 
                            WHEN INSTR(status, 'Grappling ' || character2Name) = 0 THEN status || ', Grappling ' || character2Name 
                            ELSE status 
                         END 
            WHERE characterId = :NEW.character1; 

            UPDATE Characters 
            SET status = CASE  
                            WHEN status IS NULL THEN 'Grappled by ' || character1Name 
                            WHEN INSTR(status, 'Grappled by ' || character1Name) = 0 THEN status || ', Grappled by ' || character1Name 
                            ELSE status 
                         END 
            WHERE characterId = :NEW.character2; 

        ELSIF :NEW.actionInflicted = 'Let go' THEN 
            UPDATE Characters 
            SET status = CASE 
                            WHEN status LIKE '%, Grappling ' || character2Name || '%' THEN REPLACE(status, ', Grappling ' || character2Name, '')
                            WHEN status LIKE 'Grappling ' || character2Name || ',%' THEN REPLACE(status, 'Grappling ' || character2Name || ', ', '')
                            ELSE REPLACE(status, 'Grappling ' || character2Name, '')
                         END 
            WHERE characterId = :NEW.character1; 

            UPDATE Characters 
            SET status = CASE 
                            WHEN status LIKE '%, Grappled by ' || character1Name || '%' THEN REPLACE(status, ', Grappled by ' || character1Name, '')
                            WHEN status LIKE 'Grappled by ' || character1Name || ',%' THEN REPLACE(status, 'Grappled by ' || character1Name || ', ', '')
                            ELSE REPLACE(status, 'Grappled by ' || character1Name, '')
                         END 
            WHERE characterId = :NEW.character2; 

        ELSIF :NEW.actionInflicted = 'Break free' THEN 
            UPDATE Characters 
            SET status = CASE 
                            WHEN status LIKE '%, Grappled by ' || character2Name || '%' THEN REPLACE(status, ', Grappled by ' || character2Name, '')
                            WHEN status LIKE 'Grappled by ' || character2Name || ',%' THEN REPLACE(status, 'Grappled by ' || character2Name || ', ', '')
                            ELSE REPLACE(status, 'Grappled by ' || character2Name, '')
                         END 
            WHERE characterId = :NEW.character1; 

            UPDATE Characters 
            SET status = CASE 
                            WHEN status LIKE '%, Grappling ' || character1Name || '%' THEN REPLACE(status, ', Grappling ' || character1Name, '')
                            WHEN status LIKE 'Grappling ' || character1Name || ',%' THEN REPLACE(status, 'Grappling ' || character1Name || ', ', '')
                            ELSE REPLACE(status, 'Grappling ' || character1Name, '')
                         END 
            WHERE characterId = :NEW.character2; 
        END IF; 
    END IF;
END;

create or replace trigger updateHpAfterEvent 
after insert on events 
for each row 
declare 
    ahp number(4) := :NEW.affectedHp; 
    action varchar2(50) := :NEW.actionInflicted; 
    cid number(6) := :NEW.character2; 
begin 
    IF action = 'Healed' THEN 
        UPDATE Characters 
        SET hp = GREATEST(ahp, hp + ahp) 
        WHERE characterId = cid; 
    ELSE 
        UPDATE Characters 
        SET hp = hp - ahp 
        WHERE characterId = cid; 
    END IF; 
end;

create or replace TRIGGER UpdateWeaponPositions 
AFTER DELETE ON Weapons 
BEGIN 
    UPDATE Weapons 
    SET position = position - 1 
    WHERE characterWielding = WeaponDeletionPackage.weaponBeingDeleted.characterWielding 
    AND position > WeaponDeletionPackage.weaponBeingDeleted.position; 
END;

